def overtime_goals():
    url = 'https://www.hockey-reference.com/playoffs/overtime-goals.cgi'
    text = requests.get(url).text
    parsed = BeautifulSoup(text, 'html.parser')
    franchise_table = parsed.find('div', id='div_ots')
    tbody = franchise_table.find('tbody')
    rows_tbody = tbody.find_all('tr')
    thead = franchise_table.find('thead')
    rows_thead = thead.find_all('tr')

    thead_zoz = []
    tbody_zoz = []
    for row in rows_thead:
        cells = row.find_all('th')
        for cell in cells:
            thead_zoz.append(cell.text)
    for row in rows_tbody:
        cells = row.find_all('td')
        for cell in cells:
            tbody_zoz.append(cell.text)
    updated_headers = [x if x != '' else 'Series Clinch' for x in thead_zoz]
    print(updated_headers)
    print(tbody_zoz)


def zrob_databazu():
    all_teams_table = all_teams()
    franchise_table = franchise()
    playoff_table = playoffs()
    conn = sqlite3.connect('hockey_stats.db')
    c = conn.cursor()
    # Create table
    c.execute('''
    CREATE TABLE IF NOT EXISTS team_stats (
        Team TEXT,
        GP INTEGER,
        W INTEGER,
        L INTEGER,
        OL INTEGER,
        PTS INTEGER,
        PTS_Percentage TEXT,
        GF INTEGER,
        GA INTEGER,
        SRS TEXT,
        SOS TEXT,
        RPt_Percentage TEXT,
        RW INTEGER,
        RgRec TEXT,
        RgPt_Percentage TEXT
    )
    ''')
    # Example team names and their stats (simplified for explanation)
    teams = ['Boston Bruins*', 'Toronto Maple Leafs*', 'Tampa Bay Lightning*']  # Extend this list
    stats = [
        ['82', '65', '12', '5', '135', '.823', '305', '177', '1.54', '-0.02', '.787', '54', '54-12-16', '.756'],
        ['82', '50', '21', '11', '111', '.677', '279', '222', '0.70', '0.00', '.616', '42', '42-21-19', '.628'],
        ['82', '46', '30', '6', '98', '.598', '283', '254', '0.37', '0.02', '.555', '38', '38-30-14', '.549']
    ]  # Assume this list contains corresponding stats for each team

    # Insert data into the table
    for team, stat in zip(teams, stats):
        c.execute('''
        INSERT INTO team_stats (Team, GP, W, L, OL, PTS, PTS_Percentage, GF, GA, SRS, SOS, RPt_Percentage, RW, RgRec, RgPt_Percentage)
        VALUES (?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?)
        ''', [team] + stat)

    # Commit changes and close connection
    conn.commit()
    conn.close()


